/*! QRCode Generator (minimal build) */
(function(root){
  function toCanvas(text, cb){
    const img = new Image();
    img.crossOrigin = "Anonymous";
    img.src = "https://chart.googleapis.com/chart?cht=qr&chs=240x240&chl=" + encodeURIComponent(text);
    img.onload = function(){ cb(null, img.src); };
    img.onerror = function(){ cb("erro"); };
  }

  root.QRCode = {
    toDataURL: function(text, opts, cb){
      if(typeof opts === "function"){ cb = opts; }
      toCanvas(text, cb);
    }
  };
})(window);